CREATE TABLE IF NOT EXISTS `posts` (
  `post_id` smallint(5) unsigned NOT NULL AUTO_INCREMENT,
  `post_title_en` varchar(255) NOT NULL,
  `post_title_ua` varchar(255) NOT NULL,
  `post_short_desc_en` text NOT NULL,
  `post_short_desc_ua` text NOT NULL,
  `post_full_desc_en` text NOT NULL,
  `post_full_desc_ua` text NOT NULL,
  `post_cat_id` tinyint(3) unsigned DEFAULT NULL,
  `post_date` datetime NOT NULL,
  `post_author_id` smallint(5) unsigned NOT NULL,
  `post_status` enum('post','draft') DEFAULT 'post',
  PRIMARY KEY (`post_id`),
  KEY `posts_user_u_id` (`post_author_id`),
  KEY `post_status` (`post_status`),
  KEY `post_cat_id` (`post_cat_id`)
) ENGINE=InnoDB  DEFAULT CHARSET=cp1251 AUTO_INCREMENT=24 ;

--
-- Дамп данных таблицы `posts`
