  `u_email` varchar(100) NOT NULL,
  `u_pass` varchar(255) NOT NULL,
  `u_sex` enum('Мужчина','Женщина') DEFAULT NULL,
  `u_role` enum('user','admin') NOT NULL DEFAULT 'user',
  `u_avatar` varchar(100) DEFAULT NULL,
  PRIMARY KEY (`u_id`)
) ENGINE=InnoDB  DEFAULT CHARSET=cp1251 AUTO_INCREMENT=5 ;

